package pt.ipp.isep.dei.esoft.labproject.problem.one.version.one;public class ProblemOne {    /**     * Sort an array of integers in ascending order. Implementation (1/2).     *     * @param array Array of integers.     * @return Sorted array of integers.     */    public static int[] sortArrayAscending(int[] array) {        if(array != null){            for(int i = 0; i < array.length - 1; i++){                for(int j = 0 ; j < array.length - 1 - i ; j++){                    if(array[j] > array[j+1]){                        int temp = array[j];                        array[j] = array[j+1];                        array[j + 1 ]= temp;                    }                }            }        }        return array;    }}